function InsertEBS() {
    if (!confirm("确认要付款吗？")) return false;
        $("#btnInsertEBS").prop("disabled", true);
        var safe = false;
        safe = _sysSave();
            var feeType = $("#input013").val(); //费用类型
            var appType = $("#input04").val();  //单据类型
            $.ajax({
                type: "post",
                url: "../../WorkAsp/Apply/BatchToDoNew.ashx?t=4",
                data: { "id": _mainId, "feeType": TransCost(feeType), "appType": AppCost(appType) },
                dataType: "text",
                success: function (result) {
                    if (result != 'success') {
                        if (_nodeCode == "") {
                            $.noticeAdd({
                                text: "付款成功！", stayTime: 500, onClose: function () {
                                    var str = "出纳付款";
                                    var is = _curClass.ExecSQL("select_fee_pay", "@id=" + _mainId).value; //是否存在数据
                                    if (!!is) str = "重传EBS";
                                    var state = "已通过";
                                    var nodeyj = "同意";
                                    var nodeempname = "[!EmployeeName!]";
                                    var nodeempno = "[!EmployeeCode!]";
                                    var nodedate = _curClass.ExecSQL("select_formal_datenow", "").value; //当前时间
                                    var keys = new Array();
                                    keys.push("@id=" + _mainId);
                                    keys.push("@str=" + str);
                                    keys.push("@state=" + state);
                                    keys.push("@nodeyj=" + nodeyj);
                                    keys.push("@nodeempname=" + nodeempname);
                                    keys.push("@nodeempno=" + nodeempno);
                                    keys.push("@nodedate=" + nodedate);
                                    var ret = _curClass.ExecSQL("insert_fee_pay", keys.join("|"));
                                    if (ret.error) alert("插入数据时出错！");
                                    try { window.opener.app_query(); } catch (e) { }
                                    window.close();
                                }
                            });
                        }
                    }
                },
                error: function () {
                    alert("付款失败！");
                }
            });
        return safe;
}
function InsertYS() {
    if (!confirm("您确认提交审批吗？")) return false;
    if (!$("#txtInstanceName").val()) {
        alert("请填写任务名称");
        return false;
    }
    _saveAction = "1";
    var safe = false;
    try { safe = _sysSave(); } catch (err) { alert("提交过程中出现异常：" + err.message + "\r\n请尝试重新提交，或者联系管理员。"); }
    if (safe) {
        //保存成功之后，要到数据端验证一下
        var r = _curClass.CheckData(_mainTblName, _mainId);
        if (r.error) {
            alert(r.error.Message);
            return false;
        }
        else {
            if (parseInt(r.value) == 1) {
                //调用ajax
                if (!jkManage("V")) {
                    return false;
                }
                jkManage("R");
                __doPostBack("btnSubmit", "");
                return true;
            }
            else {
                alert("提交过程中出现异常，请重新提交");
            }
        }
    }
    return false;
}

function jkManage(operate) {
    var bit = true;
    var itemArray = new Array();
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    var orgCode = $("#input09").val();
    var amount = $("#input017").val();
    for (var i = 0, l = maxOdr; i < l; i++) {
      var itemName=$("#SubTbl00_" + i + "_24").val();
      if(itemName!='')
      {
        var im = {};
        im.lineNum = $("#SubTbl00_" + i + "_16_p").text();
        im.accountCode = $("#SubTbl00_" + i + "_9").val();
        im.orgCode = $("#SubTbl00_" + i + "_27").val();
        im.itemCode = $("#SubTbl00_" + i + "_23").val();
        im.projectCode=$("#SubTbl00_" + i + "_22").val();
        im.amount=$("#SubTbl00_" + i + "_18").val()
        itemArray.push(im);
      }
    }
  if(itemArray.length>0)
  {
    $.ajax({
        url: "../../WorkAsp/Apply/BatchToDoNew.ashx?t=7",
        type: "post",
        data: { _autoId: _mainId, orgCode: orgCode, date: $("#input01").val(), itemJson: JSON.stringify(itemArray), amount: amount, operate: operate },
        dataType: "json",
        async: false,
        success: function (d) {
          var obj=eval(d);
          if(operate=="V")
          {
           if(obj.result=="E")
            {
              bit = false;
            }
          else if (obj.result=="W") {
                if (confirm("预算项目检查不通过！是否继续？")) {
                    bit = true;
                }
                else {
                    bit = false;
                }
            }
         }
          else
          {
            $.each(obj.details[0].lineDetails,function(index,item){
               var keys = new Array();
               keys.push("@type=" +item.result);
               keys.push("@appNo=" + item.lineNum);
               keys.push("@lineNO=" + obj.details[0].billCode);                 
               _curClass.ExecSQL("Update_BudgetType",item.result,item.lineNum,obj.details[0].billCode);
            });
          }
        }
    });
  }
    return bit;
}

