}
//费用类型
function ExpenseType() {
    _fnSubDelAll('T_FEE_FEE_ITEM_EXPENSE');
    var str = $("#input013").val(); //费用类型
    if (str == "普通费用") {
        setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 4, "block");
        setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 5, "block");
    }
    else {
        setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 4, "none");
        setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 5, "none");
    }
    if (str == "科室基金" || str == "捐赠支出") {
        //科室基金
        var dt = _curClass.GetDataTable("SELECT_EXPENSE_TYPE", "@itemtype=" + TransCost(str)).value;
        if (dt.Rows.length > 0) {
            var newIndex = _fnSubAdd("T_FEE_FEE_ITEM_EXPENSE");
            $("#SubTbl00_" + newIndex + "_10").val(dt.Rows[0].item_comment);  //费用项说明
            $("#SubTbl00_" + newIndex + "_9").val(dt.Rows[0].item_code);      //费用项编码
            $("#SubTbl00_" + newIndex + "_8").val(dt.Rows[0].ishavaitem);     //是否选择项目
            $("#SubTbl00_" + newIndex + "_12").val(dt.Rows[0].item_type);     //费用类型
            $("#SubTbl00_" + newIndex + "_3").val(dt.Rows[0].acct_sub);       //科目
            $("#SubTbl00_" + newIndex + "_19").val(dt.Rows[0].ID);            //费用AUTOID
            $("#SubTbl00_" + newIndex + "_20").val(dt.Rows[0].acct_commnet);  //科目说明
            $("#SubTbl00_" + newIndex + "_15").val(dt.Rows[0].sub_item_id);   //子项目编码

            mxdh(); //加入数据
            $(".T_FEE_FEE_ITEM_EXPENSE_PROJECT_SECTION").val("<请双击选择>").unbind("dblclick").bind
            ({
                focus: function () { if ($(this).val() == "<请双击选择>") { $(this).val("") }; },
                blur: function () { if ($(this).val() == "") { $(this).val("<请双击选择>") }; },
                dblclick: function () {
                    var temp1 = "description,flex_value";
                    var id = $(this).prop("id");
                    var sub15 = id.substring(0, id.lastIndexOf('_') + 1) + "15";
                    var sub17 = id.substring(0, id.lastIndexOf('_') + 1) + "17";
                    var cid = sub17 + "," + sub15;
                    _openCenter("../../WorkAsp/select/Expense/Select_ProPeroid.aspx?tblName=Q_HRP_BC_DEPT_FUND&cid=" + cid + "&queryfield=" + temp1 + "&condition=" + connectionProPeroid(sub15), "", 900, 600);
                }
            });
        }
    }
}

//列隐藏
function setHiddenCol(oTable, iCol, v) {
    for (i = 0; i < oTable.rows.length ; i++) {
        oTable.rows[i].cells[iCol].style.display = v;
        //如果该列隐藏则让其显示，反之则让其隐藏
        //oTable.rows[i].cells[iCol].style.display=="none"?"block":"none";
    }
}


//单据类型转换
function AppCost(val) {
    var str = "";
    if (val == "日常报销")
        str = "1";
    else if (val == "科研报销")
        str = "5";
    return str;
}
//费用类型转换
function TransCost(val) {
    var str = "";
    if (val == "普通费用")
        str = "0";
    else if (val == "科室基金")
        str = "1";
    else if (val == "往来款")
        str = "2";
    else if (val == "捐赠支出")
        str = "3";
    else if (val == "教学")
        str = "4";
    else if (val == "科研")
        str = "5";
    else if (val == "其他科研项目")
        str = "6";
    else if (val == "财政")
        str = "7";
    return str;
}
function connectionProPeroid(sub15) {
    var felxValue = '';
    var sql = new Array();
    var sub_item_id = $("#" + sub15).val();  //子项目编码
    if ($("#input013").val() == "普通费用") {
        var usercode = $("#input011_p").text();
        sql.push(" _userName ='" + usercode + "'");
    }
    else if ($("#input013").val() == "科室基金") {
        sql.push(" flex_value like '9002%'");
    }
    else if ($("#input013").val() == "往来款") {
        // sql.push(" flex_value like '9004%'");
        felxValue = '40';
        if (sub_item_id > 0) felxValue = sub_item_id;
        sql.push(" flex_value like '" + felxValue + "%'");
    }
    else if ($("#input013").val() == "捐赠支出") {
        sql.push(" flex_value like '9001%'");
    }
    else if ($("#input013").val() == "教学") {
        felxValue = '22';
        if (sub_item_id > 0) felxValue = sub_item_id;
        if (felxValue.length > 6) felxValue = felxValue.substring(0, 4);
        sql.push(" flex_value like '" + felxValue + "%'");
    }
    else if ($("#input013").val() == "其他科研项目") {
        sql.push(" flex_value like '21%' and attribute2 is null");
    }
    else if ($("#input013").val() == "财政") {
        felxValue = '1';
        if (sub_item_id > 0) felxValue = sub_item_id;
        sql.push(" flex_value like '" + felxValue + "%'");
    }
    return escape(sql.join("").replace(/'/gi, "[QUOTES]"));
}
function _sysBeforeSave() {
    var maxOdr = $("#T_FEE_FEE_ITEM_EXPENSE").find("tr").length;
    if (maxOdr < 3) {
        alert("请添加费用项！");
        return false;
    }
    if (_nodeCode == "A1") {
        if (_getNumById("input029") > _getNumById("input017")) {
            alert("个税不能大于报销金额！");
            return false;
        }
    }
    if (fee_select()) return false;
    if (_isNew) info_autonum();
    if (_nodeCode == 'A0') {
        var datenow = _curClass.ExecSQL("select_formal_datenow", "").value;  //当前时间
        $("#input01").val(datenow);
    }
    return true;
}
