//科室类型转换
function DeptType(val) {
    str = "";
    if (val == "行政管理部门" || val == "资产负债表经营单元") {
        str = "AD";
    }
    else if (val == "医疗技术类科室" || val == "医疗辅助类科室" || val == "临床服务类科室") {
        str = "CL";
    }
    return str;
}
function connection() {
    var sql = new Array();
    var checktype = $("#input013").val(); //费用类型
    var fundId = $("#input046").val();    //基金来源ID
    sql.push(" item_type=" + TransCost(checktype) + "");
    if (checktype == "普通费用") {
        var depttype = _curClass.ExecSQL("SELECT_DEPT_TYPE", "@deptcode=" + $("#input09").val()).value;
        if (!!depttype) {
            var temp = DeptType(depttype);
            if (!!temp) {
                sql.push(" and dept_type_code='" + temp + "'");
            }
        }
    }
    if (_sIndex == '2') {
        sql.push(" and fund_id=" + fundId + "");
    }
    return escape(sql.join("").replace(/'/gi, "[QUOTES]"));
}
function mxdh() {
    keysnum = [];
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    for (var i = 0, l = maxOdr; i < l; i++) {
        keysnum.push($("#SubTbl00_" + i + "_19").val());  //费用AUTOID
    }
}
//费用回填
function select_callback_zq(arr) {
    mxdh();
    for (var i = 0; i < arr.length; i++) {
        var key = arr[i];
        if ($.inArray(key.split("@")[6], keysnum) < 0) {
            if (feebs == "start") {
                $("#" + rowId + "10").val(key.split("@")[0]);  //费用项说明
                $("#" + rowId + "9").val(key.split("@")[1]);   //费用项编码
                $("#" + rowId + "8").val(key.split("@")[2]);   //是否选择项目
                $("#" + rowId + "12").val(key.split("@")[3]);  //费用类型
                $("#" + rowId + "3").val(key.split("@")[8]);   //科目
                $("#" + rowId + "19").val(key.split("@")[6]);  //费用AUTOID
                $("#" + rowId + "20").val(key.split("@")[7]);  //科目说明
                $("#" + rowId + "15").val(key.split("@")[9]);  //子项目编码
                
                ShowYs(key,rowId.substr(rowId.length-2,1));
                //判断是否有项目段，如果没有隐藏项目段
                if ($("#" + rowId + "8").val() != "1") {
                    $("#" + rowId + "17").hide();
                }
                else {
                    //setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 3,"none");
                    $(".T_FEE_FEE_ITEM_EXPENSE_PROJECT_SECTION").val("<请双击选择>").unbind("dblclick").bind
                    ({
                        focus: function () { if ($(this).val() == "<请双击选择>") { $(this).val("") }; },
                        blur: function () { if ($(this).val() == "") { $(this).val("<请双击选择>") }; },
                        dblclick: function () {
                            var temp1 = "description,flex_value";
                            var id = $(this).prop("id");
                            var sub15 = id.substring(0, id.lastIndexOf('_') + 1) + "15";
                            var sub17 = id.substring(0, id.lastIndexOf('_') + 1) + "17";
                            var cid = sub17 + "," + sub15;
                            _openCenter("../../WorkAsp/select/Expense/Select_ProPeroid.aspx?tblName=Q_HRP_BC_DEPT_FUND&cid=" + cid + "&queryfield=" + temp1 + "&condition=" + connectionProPeroid(sub15), "", 900, 600);
                        }
                    });
                }
            }
            else {
                var newIndex = _fnSubAdd("T_FEE_FEE_ITEM_EXPENSE");
                $("#SubTbl00_" + newIndex + "_10").val(key.split("@")[0]);  //费用项说明
                $("#SubTbl00_" + newIndex + "_9").val(key.split("@")[1]);   //费用项编码
                $("#SubTbl00_" + newIndex + "_8").val(key.split("@")[2]);   //是否选择项目
                $("#SubTbl00_" + newIndex + "_12").val(key.split("@")[3]);  //费用类型
                $("#SubTbl00_" + newIndex + "_3").val(key.split("@")[8]);   //科目
                $("#SubTbl00_" + newIndex + "_19").val(key.split("@")[6]);  //费用AUTOID
                $("#SubTbl00_" + newIndex + "_20").val(key.split("@")[7]);  //科目说明
                $("#SubTbl00_" + newIndex + "_15").val(key.split("@")[9]);  //子项目编码    
                 ShowYs(key,newIndex);
                //判断是否有项目段，如果没有隐藏项目段
                if ($("#SubTbl00_" + newIndex + "_8").val() != "1") {
                    $("#SubTbl00_" + newIndex + "_17").hide();
                }
                else {
                    // setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 3,"none");
                    $(".T_FEE_FEE_ITEM_EXPENSE_PROJECT_SECTION").val("<请双击选择>").unbind("dblclick").bind
                    ({
                        focus: function () { if ($(this).val() == "<请双击选择>") { $(this).val("") }; },
                        blur: function () { if ($(this).val() == "") { $(this).val("<请双击选择>") }; },
                        dblclick: function () {
                            var temp1 = "description,flex_value";
                            var id = $(this).prop("id");
