                            _openCenter("../../WorkAsp/select/Expense/Select_ProPeroid.aspx?tblName=Q_HRP_BC_DEPT_FUND&cid=" + cid + "&queryfield=" + temp1 + "&condition=" + connectionProPeroid(sub15), "", 900, 600);
                        }
                    });
                }
            }
        }
        else {
            alert("费用项已存在！");
        }
    }
