function InsertEBS() 
{
    if (!confirm("确认要付款吗？")) return false;
    $("#btnInsertEBS").prop("disabled", true);
    var safe = false;
    safe = _sysSave();
    var feeType = $("#input013").val(); //费用类型
    var appType = $("#input04").val();  //单据类型
    $.ajax({
        type: "post",
        url: "../../WorkAsp/Apply/BatchToDo.ashx?t=4",
        data: { "id": _mainId, "feeType": TransCost(feeType), "appType": AppCost(appType) },
        dataType: "text",
        success: function (result) 
           {
            if (result != 'success')
             {
                if (_nodeCode == "") 
                {
                    $.noticeAdd(
                      {
                        text: "付款成功！", stayTime: 500, onClose: function () 
                        {
                            var str = "出纳付款";
                            var is = _curClass.ExecSQL("select_fee_pay", "@id=" + _mainId).value; //是否存在数据
                            if (!!is) str = "重传EBS";
                            var state = "已通过";
                            var nodeyj = "同意";
                            var nodeempname = "[!EmployeeName!]";
                            var nodeempno = "[!EmployeeCode!]";
                            var nodedate = _curClass.ExecSQL("select_formal_datenow", "").value; //当前时间
                            var keys = new Array();
                            keys.push("@id=" + _mainId);
                            keys.push("@str=" + str);
                            keys.push("@state=" + state);
                            keys.push("@nodeyj=" + nodeyj);
                            keys.push("@nodeempname=" + nodeempname);
                            keys.push("@nodeempno=" + nodeempno);
                            keys.push("@nodedate=" + nodedate);
                            var ret = _curClass.ExecSQL("insert_fee_pay", keys.join("|"));
                            if (ret.error) alert("插入数据时出错！");
                            try { window.opener.app_query(); } catch (e) { }
                            window.close();
                        }
                    });
                }
            }
        },
        error: function () 
        {
            alert("付款失败！");
        }
    });
    return safe;
}
function InsertYS() 
{
    if (!confirm("您确认提交审批吗？")) return false;
    //验证含有预算项目的是否选择预算项，若未选择则return false
    var bit = ValidateBudgetItem();
    if (!bit) 
    {
        alert("含有预算项目预算项目为必选项！！");
        return false;
    }
    _saveAction = "1";
    var safe = false;
    safe = _sysSave();
    if (safe) 
    {
        //调用ajax
        if (!YsManage("V")) 
        {
            return false;
        }
        YsManage("R");
        __doPostBack("btnSubmit", "");
        return true;
    }
    return false;
}


