function choseType() {
    var bs = true;
    if (!!$("#input013").val()) {
        if (_sIndex == '2') {
            if ($("#input030").val() == "") {
                alert("请选择科研课题项！");
                $("#input038").css({ "background-color": "#fafcaa" });
                bs = false;
            }
        }
        else {
            if ($("#input013").val() == "普通费用" || $("#input013").val() == "还款") {
                if ($("#input09").val() == "") {
                    alert("请选择申领科室！");
                    $("#input033").css({ "border-color": "red", "border-width": "1px" });
                    bs = false;
                }
            }
        }
    }
    else {
        alert("请选择费用类型！");
        $("#input013").focus();
        bs = false;
    }
    return bs;
}
