    $("#input03").val(num).css("color", "blue");
    jQuery("row:first", _xmlData).find("APP_NO").text(num);
    $("#input03").change();
}
//支付方式
function PAY_METHOD(ctl) 
{
    var val = $(ctl).val();
    if (!!val) 
    {
        if (val == "现金支付") 
        {
            if (_isNew) 
            {
                str = new Array();
                $("#input024").val("");
                $("#input024").val("[!EmployeeName!]");
                strs($("#input024").val());
            }
            $(".ry").show();
            $(".gys,.gysandry").hide();
        }
        else if (val == "支票支付") 
        {
            if (_isNew) 
            {
                str = new Array();
                $("#input024").val("");
                $("#input024").val("[!EmployeeName!]");
                strs($("#input024").val());
            }
            $(".gysandry").show(); $(".ry,.gys").hide();
        }
        else if (val == "建行" || val == "民口科技专户" || val == "汇款" || val == "委托收款") 
        {
            if (_isNew) 
            {
                str = new Array();
                $("#input024").val("");
            }
            $(".gys").show();
            $(".ry,.gysandry").hide();
        }
    }
}
