function strs(bs)
{
    var type = $("#input013").val();
    if (type == "还款") 
    {
        str = new Array();
    }
    str.push(bs);
    $("#input024").val(str.join(","));
}
//申请单据号
function info_autonum()   //并发操作   单号处理
{
    var valnumtemp = "BX";
    var keys = [];
    keys.push("@cglx=" + valnumtemp);
    keys.push("@num=4");
    var num = _curClass.ExecSQL("SELECT_FEE_EXPENSE_NUM", keys.join("|")).value;
    $("#input03").val(num).css("color", "blue");
    jQuery("row:first", _xmlData).find("APP_NO").text(num);
    $("#input03").change();
}
//支付方式
function PAY_METHOD(ctl) 
{
    var val = $(ctl).val();
    if (!!val) 
    {
        if (val == "现金支付") 
        {
            if (_isNew) 
            {
                str = new Array();
                $("#input024").val("");
                $("#input024").val("[!EmployeeName!]");
                strs($("#input024").val());
            }
            $(".ry").show();
            $(".gys,.gysandry").hide();
        }
        else if (val == "支票支付") 
        {
            if (_isNew) 
            {
                str = new Array();
                $("#input024").val("");
                $("#input024").val("[!EmployeeName!]");
                strs($("#input024").val());
            }
            $(".gysandry").show(); $(".ry,.gys").hide();
        }
        else if (val == "建行" || val == "民口科技专户" || val == "汇款" || val == "委托收款") 
        {
            if (_isNew) 
            {
                str = new Array();
                $("#input024").val("");
            }
            $(".gys").show();
            $(".ry,.gysandry").hide();
        }
    }
}
