function read() 
{
    $("#input033").unbind("dblclick");    //申请科室
    $("#input03").prop("readonly", true);  //申请单号
    $("#input025").prop("readonly", true); //报销事由
    $("#input013").prop("disabled", true); //费用类型
    $("#input05").prop("readonly", true);  //附件数量
    $("#input022").prop("readonly", true); //实际付款金额
    $("#input026").prop("readonly", true); //备注
    $(".T_FEE_FEE_ITEM_EXPENSE_ITEM_CODE").prop("readonly", true);    //费用项编码
    $(".T_FEE_FEE_ITEM_EXPENSE_ITEM_COMMENT").prop("readonly", true); //费用项说明
    //出纳付款不可以修改费用项,重传EBS可以修改费用项
    if (_sys.getValue("STATUS") == "未付款") 
    {
        $(".T_FEE_FEE_ITEM_EXPENSE_ITEM_COMMENT").css("width", "");    //补上修改图片的空缺宽度
        $(".T_FEE_FEE_ITEM_EXPENSE_Money").prop("readonly", true);     //报销金额
    }
    else 
    {
        $("#addFY,.fee").show();  //添加-修改费用项
        $(".T_FEE_EXPENSE_INVOICE_FEE_TYPE").prop("disabled", false);  //费用类型
      //  var itemwidth = $(".T_FEE_FEE_ITEM_EXPENSE_ITEM_COMMENT").css("width").replace("px", "") - 65;
      //  $(".T_FEE_FEE_ITEM_EXPENSE_ITEM_COMMENT").css("width", itemwidth);
    }
    $(".qc").hide();  //清除
    var t1 = $("#T_FEE_FEE_ITEM_EXPENSE");
    $(".ADDBTN", t1).hide();
    $(".DELBTN", t1).hide();
}
function write() 
{
    $(".cashierPay").show(); //个税.支票/汇票号.记账日期
    $("#input029").prop("readOnly", false); //个税
    $(".kjkm").show();       //子表会计科目
    $(".Payment").css("color", "blue");
}
//数字验证
function num(ctl) 
{
    if (!!ctl.value) {
        var reg = /^\d+(\.{0,1}\d+){0,1}$/;  //非负数
        if (!!isNaN(ctl.value.replace(/,/g, "")) || !reg.test(ctl.value.replace(/,/g, ""))) 
        {
            alert("必须为数字！");
            $(ctl).focus().select();
        }
        else 
        {
            var reg = /^[0-9]*[1-9][0-9]*$/;   //正整数
            if ($(ctl).prop("class").indexOf("T_FEE_EXPENSE_INVOICE_ATTA_NUM") >= 0)
            {
                if (!reg.test(ctl.value)) {
                    alert("附件必须为正整数！");
                    $(ctl).focus().select();
                }
            }
        }
    }
}
//子表项目段刷新
function xmd()
{
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    for (var i = 0, l = maxOdr; i < l; i++) {
        //判断是否有项目段，如果没有隐藏项目段
        if ($("#SubTbl00_" + i + "_8").val() != "1")
        {
            $("#SubTbl00_" + i + "_17").hide();
        }
        else 
        {
            $(".T_FEE_FEE_ITEM_EXPENSE_PROJECT_SECTION").unbind("dblclick").bind
            ({
                focus: function () { if ($(this).val() == "<请双击选择>") { $(this).val("") }; },
                blur: function () { if ($(this).val() == "") { $(this).val("<请双击选择>") }; },
                dblclick: function ()
              {
                    var temp1 = "description,flex_value";
                    var id = $(this).prop("id");
