//申请科室回填
function select_dept_callback(bs) {
    if (bs != deptname) {
        $("#input033").css("border-width", "0px");
        if ($("Table[TableName=T_FEE_FEE_ITEM_EXPENSE]>row", _xmlData).length > 0) {
            if (!!confirm("您已经选择【" + deptname + "】的费用项,继续操作将删除费用项！")) {
                deptname = bs;
                _fnSubDelAll('T_FEE_FEE_ITEM_EXPENSE');
            }
            else {
                $("#input033").val(deptname);
                $("#input09").val(deptcode);
            }
        }
        else {
            deptname = bs;
        }
    }
}
//申请科室
function ApplyDept(hospitaltype) {
    var deptcode_temp = '';
    if (_isNew) {
        var dtinfo = _curClass.GetDataTable("select_dept_info", "").value;
        if (dtinfo.Rows.length > 0) {
            $("#input033").val(dtinfo.Rows[0].DeptName);  //申领部门名称
            $("#input09").val(dtinfo.Rows[0].DeptCode);   //申领部门编码
            if (dtinfo.Rows[0].DeptCode == "901") {
                deptcode_temp = '901';
                $("#input033").val('');
                $("#input09").val('');
            }
        }
    }
    if (_nodeCode == "" || _nodeCode == "A0") {
        var temp1 = "DeptCode,DeptName";
        var temp1_1 = "input09,input033";
        $("#input033").css("color", "#606060").bind
        ({
            focus: function () { if ($(this).val() == "<请双击选择>") { $(this).val("") }; },
            blur: function () { if ($(this).val() == "") { $(this).val("<请双击选择>").css("color", "#606060") }; },
            keyup: function () { $(this).css("color", "black"); },
            dblclick: function () {
                deptname = $(this).val();
                deptcode = $("#input09").val();
                if (deptcode_temp == "901") {
                    _openCenter("../../WorkAsp/select/General/Select_Radio_Dept.aspx?tblName=Q_HRP_BC_DEPT_QT&queryfield=" + temp1 + "&cid=" + temp1_1 + "&Defaultvalue=@hospitaltype=" + hospitaltype, "", 900, 600);
                }
                else {
                    _openCenter("../../WorkAsp/select/General/Select_Radio_Dept.aspx?tblName=Q_HRP_BC_DEPT&queryfield=" + temp1 + "&cid=" + temp1_1 + "&Defaultvalue=@hospitaltype=" + hospitaltype, "", 900, 600);
                }
            }
        });
    }
}
//子表项目段刷新
function fee_select() {
    var bs = false;
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    for (var i = 0, l = maxOdr; i < l; i++) {
        //判断是否有项目段，如果有就为必填项
        if ($("#SubTbl00_" + i + "_8").val() == 1 && ($("#SubTbl00_" + i + "_17").val() == "<请双击选择>" || $("#SubTbl00_" + i + "_17").val() == "")) {
            alert("项目段不能为空！");
            $("#SubTbl00_" + i + "_17").focus().select();
            bs = true;
            break;
        }
    }
    return bs;
}

function ShowYs(key, newIndex) {
    $("#SubTbl00_" + newIndex + "_28").val("");  
    //判断预算列是否显示。
    var budgetCodes = key.split("@")[10];
    AutoWidth("add",budgetCodes,newIndex);
    if (!!budgetCodes) 
    {
      var bcodes=budgetCodes.split(',').join("','");
        $.ajax({
            url: "../../WorkAsp/Apply/BatchToDo.ashx?t=8",
            type: "post",
            data: { orgCode: $("#input09").val(), date: $("#input01").val(), budgetItemCode: bcodes },
            dataType: "text",
            success: function (result) {
                //执行查询可选项数据。
                var dt = _curClass.GetDataTable("SelectUseItems", "@userName=" + $("#input011_p").text()).value;
                if (dt.Rows.length == 1) {
                    $("#SubTbl00_" + newIndex + "_24").show();
                    $("#SubTbl00_" + newIndex + "_23").val(dt.Rows[0].BudgetItemCode);
                    $("#SubTbl00_" + newIndex + "_24").val(dt.Rows[0].BudgetItemName);
                    $("#SubTbl00_" + newIndex + "_25").val(dt.Rows[0].ProjectCode);
                    $("#SubTbl00_" + newIndex + "_26").val(dt.Rows[0].ProjectName);
                    $("#SubTbl00_" + newIndex + "_27").val(dt.Rows[0].BudgetOrgCode);
                    $("#SubTbl00_" + newIndex + "_28").val(dt.Rows[0].BudgetOrgName);
                }
                else if (dt.Rows.length > 1) {
                    //如果个数为1 则直接赋值，不再给双击事件。  
                    $("#SubTbl00_" + newIndex + "_24").val("<请双击选择>");
                    $("#SubTbl00_" + newIndex + "_24").show();
                    $(".T_FEE_FEE_ITEM_EXPENSE_BudgetItemName").unbind("dblclick").bind({
                        focus: function () { if ($(this).val() == "<请双击选择>") { $(this).val("") }; },
                        blur: function () { if ($(this).val() == "") { $(this).val("<请双击选择>") }; },
                        dblclick: function () {
                            $.ajax({
                                url: "../../WorkAsp/Apply/BatchToDo.ashx?t=8",
                                type: "post",
                                data: { orgCode: $("#input09").val(), date: $("#input01").val(), budgetItemCode: bcodes },
                                dataType: "text",
                                success: function (result) { }
                            });
                            var temp1 = "BudgetItemCode,BudgetItemName,ProjectCode,ProjectName,BudgetOrgCode,BudgetOrgName";
                            var id = $(this).prop("id");
                            var sub23 = id.substring(0, id.lastIndexOf('_') + 1) + "23";//BudgetItemCode
                            var sub24 = id.substring(0, id.lastIndexOf('_') + 1) + "24";//BudgetItemName
                            var sub25 = id.substring(0, id.lastIndexOf('_') + 1) + "25";//ProjectCode
                            var sub26 = id.substring(0, id.lastIndexOf('_') + 1) + "26";//ProjectName
                            var sub27 = id.substring(0, id.lastIndexOf('_') + 1) + "27";//BudgetOrgCode
                            var sub28 = id.substring(0, id.lastIndexOf('_') + 1) + "28";//BudgetOrgName
                            var cid = sub23 + "," + sub24 + "," + sub25 + "," + sub26 + "," + sub27 + "," + sub28;
                            _openCenter("../../WorkAsp/select/Expense/Select_KMRadio(dx).aspx?tblName=Q_Item_Budget_JK&cid=" + cid + "&queryfield=" + temp1 + "&condition=" + connectionProPeroid(sub23), "", 900, 600);

                        }
                    });
                }
            }
        });
    }
}

//预算项目验证及冻结调用
function YsManage(operate) {
    var bit = true;
    var itemArray = new Array();
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    var orgCode = $("#input09").val();
    var amount = $("#input017").val();
    for (var i = 0, l = maxOdr; i < l; i++) {
        var itemName = $("#SubTbl00_" + i + "_24").val();
        if (!!itemName) {
            var im = {};
            im.lineNum = $("#SubTbl00_" + i + "_16_p").text();
            im.accountCode = $("#SubTbl00_" + i + "_9").val();
            im.orgCode = $("#SubTbl00_" + i + "_27").val();
            im.itemCode = $("#SubTbl00_" + i + "_23").val();
            im.projectCode = $("#SubTbl00_" + i + "_22").val();
            im.amount = $("#SubTbl00_" + i + "_18").val()
            itemArray.push(im);
        }
    }
    if (itemArray.length > 0)
    {
        $.ajax({
            url: "../../WorkAsp/Apply/BatchToDo.ashx?t=7&sjs=" + new Date().getTime(),
            type: "post",
            data: { _autoId: _mainId, orgCode: orgCode, date: $("#input01").val(), tablebs: "BX", amount: amount, operate: operate },
            dataType: "json",
            async: false,
            success: function (d) {
                var obj = eval(d);
                if (operate == "V") {
                    if (obj.result == "E")
                    {
                        bit = false;
                    }
                    else
                    {
                        if (obj.result == "W")
                        {
                            if (confirm("预算提示:超出预算！是否继续？"))
                            {
                                bit = true;
                            }
                            else
                            {
                                bit = false;
                            }
                        }
                        if (!!bit)
                        {
                            $.each(obj.details[0].lineDetails, function (index, item) {
                                var keys = new Array();
                                keys.push("@type=" + (item.result == "S" ? "预算内" : "预算外"));
                                keys.push("@lineNO=" + item.lineNum);
                                keys.push("@appNo=" + obj.details[0].billCode);
                                _curClass.ExecSQL("Update_BudgetType", keys.join("|"));
                            });
                        }
                    }
                }
            }
        });
    }
    return bit;
}

//查看预算事件。
$("#addView").click(function () {
    //调用接口获取token拼接url
    var token = "";
    $.ajax({
        url: "../../WorkAsp/Apply/BatchToDo.ashx?t=10&sjs=" + new Date().getTime(),
        post: "post",
        async: false,
        dataType: "json",
        success: function (d) {
            var obj = eval(d);
            token = obj.token;
        }
    });
    var itemArray=new Array();
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    for (var i = 0, l = maxOdr; i < l; i++) {
        var itemName = $("#SubTbl00_" + i + "_24").val();
        if (!!itemName) {
            var im = {};
            im.lineNum = $("#SubTbl00_" + i + "_16_p").text();
            im.accountCode = $("#SubTbl00_" + i + "_9").val();
            im.orgCode = $("#SubTbl00_" + i + "_27").val();
            im.itemCode = $("#SubTbl00_" + i + "_23").val();
            im.projectCode = $("#SubTbl00_" + i + "_22").val();
            im.amount = $("#SubTbl00_" + i + "_18").val()
            itemArray.push(im);
            break;
        }
    }
    var year = $("#input01").val().substring(0, 4) + "00";
    var bgOrgCode = itemArray[0].orgCode;
    _openCenter("http://172.27.1.55/bms/bbudget/year/report/a/ui/?orgCode=" + bgOrgCode + "&period=" + year + "&" + token, "_blank", 1000, 800);
});

function ValidateBudgetItem() {
    var bit = true;
    var maxOdr = _sysModel[0].subtbls[0].maxorder;
    for (var i = 0; i < maxOdr; i++) {
        var obj = $("#SubTbl00_" + i + "_24");
        if (!!obj) {
            var budgetItem = $("#SubTbl00_" + i + "_24").val();
            if (budgetItem == '<请双击选择>') {
                bit = false;
                break;
            }
        }
    }
    return bit;
}

//
function AutoWidth(type,budgetCodes,newIndex)
{
     var str = $("#input013").val(); //费用类型
  if(type=="load")
  {
      if (str == "普通费用" || str == "还款") 
      {

        if(_nodeCode == 'A8'||_nodeCode == 'A1')
        {
         setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 2, "block","110px");
        }
        else
        {
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 2, "block","110px");
        }
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 4, "block");
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 5, "block");

      }
      else 
      {
        if(_sIndex==0)
        {
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 2, "block","240px");
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 4, "none");
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 5, "none");
        }
        if(_sIndex==2)
        {
          setHiddenCol($("#T_FEE_FEE_ITEM_EXPENSE")[0], 2, "block","240px");
        }
      }
  }
  else if(type="add")
  {
        if (!budgetCodes) 
        {
          $("#SubTbl00_" + newIndex + "_24").hide();
          $("#SubTbl00_" + newIndex + "_26").hide();
        }
       if (str == "普通费用" || str == "还款")
       {
         $("#SubTbl00_" + newIndex + "_10").width("110px");
       }
  }
}

//列隐藏
function setHiddenCol(oTable, iCol, v,width) {
    for (i = 0; i < oTable.rows.length ; i++) {
        oTable.rows[i].cells[iCol].style.display = v;
      if(!!width)
      {
       $("#SubTbl00_" + i + "_10").width(width);
      }
    }
}

TableStyle.ListScriptBlock:
function fnSel(val,row)
{
   var autoid=$(row).attr("id");                //主键
   var flowState=$("_wfstate", row).text();     //流程表单提交状态
   var fee_type=$("FEE_TYPE",row).text();
   var app_type=$("APP_TYPE", row).text();      //单据类型
   var hos_type=$("Hospital_Type", row).text(); //院区标识(1:人民,0:海淀)
   var instanceId=$("InstanceId", row).text();  //流程实例ID  
   var state=$("STATUS", row).text();           //状态
   var str=new Array();
   if(flowState.indexOf("处理中")>=0)
   {
      str.push("<a title='A5打印申请单' href=\"javascript:wf_A5print('"+autoid+"')\">A5打印申请单</a>");
      str.push("<a title='打印申请单' href=\"javascript:wf_print('"+instanceId+"','"+app_type+"','"+hos_type+"','"+fee_type+"','"+autoid+"')\">打印申请单</a>");
   }
   else
   {
      if(!!flowState)
      {
         if(flowState.indexOf("未发起")>=0)
            str.push("<a class='red' href=\"javascript:del('"+autoid+"','"+flowState+"')\">删除</a>");
         else
         {
           str.push("<a title='A5打印申请单' href=\"javascript:wf_A5print('"+autoid+"')\">A5打印申请单</a>");
            str.push("<a title='打印申请单' href=\"javascript:wf_print('"+instanceId+"','"+app_type+"','"+hos_type+"','"+fee_type+"','"+autoid+"')\">打印申请单</a>");
         }
      }
      else
         str.push("<a class='red' href=\"javascript:del('"+autoid+"','"+flowState+"')\">删除</a>");
   }
   return str.join("|");
}
function wf_A5print(autoid)
{
   var url = "AppDetailTest.aspx?tblName=T_FEE_EXPENSE_INVOICE&sindex=4&condition=_autoid='"+autoid+"'";
   openCenter(url, "_blank", 1000, 700);
}

function app_wfinfo(autoid)
{
   var url = "AppWorkFlowInfo.aspx?tblName=T_FEE_EXPENSE_INVOICE&mainId=" + autoid;
   openCenter(url,"_blank",1000,700);
}
function wf_print(instanceId,type,hos_type,fee_type,autoid)
{
  var url ="";
  if(hos_type=="1")
  {
     if(type=='日常报销')
     { 
       if(fee_type=="还款")
       {
         //url = "AppWorkflowPrint.aspx?InstanceId=" + instanceId+"&sindex=3";
         url = "AppDetail.aspx?tblName=T_FEE_EXPENSE_INVOICE&sindex=3&condition=_autoid='"+autoid+"'";
       }
        else
        {
          url = "AppWorkflowPrint.aspx?InstanceId=" + instanceId;
        }
     }
     else
     {
       url = "AppWorkflowPrint.aspx?InstanceId=" + instanceId+"&sindex=3";
     }
  }
  else
  {
     url = "AppWorkflowPrint.aspx?InstanceId=" + instanceId+"&sindex=1";
  }
  openCenter(url, "_blank", 1000, 700);
}
function del(id,flowState)
{
   if(flowState.indexOf("处理中")>=0)
   {
      alert("删除出错：本条记录已经提交，无法删除！");
      return;
   }
   if(!!confirm('确定删除这条记录吗?'))
   {
      var ret=_curClass.DelRecord("T_FEE_EXPENSE_INVOICE",id);
      if(ret.error)
      {
          alert("删除出错："+ret.error.Message);
      }
      else
      {
          $.noticeAdd({ text: '删除成功！'});
          $("#flex1").flexReload();
      }
   }
}

function app_add(cmd, grid)
{
  if(_sIndex==0)
  {
    openCenter("../workflow/NewFlow.aspx?workflowCode=HRP_FYGL_RCBX&_rnd=" + Math.random(), "_blank", 1000, 700);
  }
  else if(_sIndex==1)
  {
    openCenter("../workflow/NewFlow.aspx?workflowCode=HRP_FYGL_HDRCBX&_rnd=" + Math.random(), "_blank", 1000, 700);
  }
  else if(_sIndex==2)
  {
    openCenter("../workflow/NewFlow.aspx?workflowCode=HRP_FYGL_KYBX&_rnd=" + Math.random(), "_blank", 1000, 700);
  }
}
